                        $maxDiscount = 0;
                        if($plan->discount_type && $plan->discount_type === 1 && $plan->discont_const){
                            $maxDiscount = min($plan->discont_const, $plan->price);
                        }elseif(!is_null($plan->discount_type) && $plan->discount_type === 0 && $plan->discont_percenet){
                            $discount = min($plan->price * $plan->discont_percenet / 100, is_null($plan->max_discont_percenet) ? $plan->price : $plan->max_discount_percent);
                            $maxDiscount = min($discount, $plan->price);
                        }
                        $price = $plan->price - $maxDiscount;
                        $tax = $price / 10 ;
                        $totalPrice = $price + $tax;
